// Importez les dépendances nécessaires
import React, { useState } from "react";
import { Text, Button } from "../atomes";
import { Left, Right } from "../atomes/Button"; // Ajoutez les composants Left et Right pour les boutons du carousel

// Importez les images nécessaires
const bg1 = require("../../assets/bg-1.png");
const bg2 = require("../../assets/bg-2.png");

// Composant Carousel
const Carousel = () => {
  // État pour gérer le numéro d'affichage du carousel
  const [display, setDisplay] = useState(1);

  // Fonction pour changer le numéro d'affichage du carousel
  const changeDisplay = () => {
    if (display === 1) {
      setDisplay(2);
    } else if (display === 2) {
      setDisplay(1);
    }
  };

  // Rendu du composant
  return (
    <div
      className={`w-full h-[90vh] transition-background duration-500 ease-in-out`}
      style={
        display === 1
          ? {
              background: `url(${bg1})`,
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
            }
          : {
              background: `url(${bg2})`,
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
            }
      }
    >
      <div className="flex flex-row justify-between relative top-1/2 px-10">
        <Left onClick={() => changeDisplay()} />
        <Right onClick={() => changeDisplay()} />
      </div>
      <div className="select-none inline absolute top-1/3 left-48">
        <div>
          {display === 1 ? (
            <Text.h3 color={"white"} fontSize={50}>
              Nouveautés 2023
            </Text.h3>
          ) : (
            <Text.h3 color={"white"} fontSize={50}>
              En Promotions
            </Text.h3>
          )}
        </div>
        <div>
          {display === 1 ? (
            <Text.label color={"white"} fontSize={32}>
              Dom Pérignon
            </Text.label>
          ) : (
            <Text.label color={"white"} fontSize={32}>
              Gaillac 2020
            </Text.label>
          )}
        </div>
        <div className="mt-4">
          <Button.Primary value={"Voir Plus"} />
        </div>
      </div>
    </div>
  );
};

export { Carousel };
