const h1 = ({ children, color, align, textDecoration }) => {
  return (
    <h1
      style={{
        color: color || "black",
        textAlign: align || "left",
        fontSize: 32,
        textDecorationLine: textDecoration,
      }}
    >
      {children}
    </h1>
  );
};

const h2 = ({ children, color, align, textDecoration }) => {
  return (
    <h2
      style={{
        color: color || "black",
        textAlign: align || "left",
        fontSize: 26,
        textDecorationLine: textDecoration,
      }}
    >
      {children}
    </h2>
  );
};

const h3 = ({ children, color, align, textDecoration }) => {
  return (
    <h3
      style={{
        color: color || "black",
        textAlign: align || "left",
        fontSize: 22,
        textDecorationLine: textDecoration,
      }}
    >
      {children}
    </h3>
  );
};

const h4 = ({ children, color, align, textDecoration }) => {
  return (
    <h4
      style={{
        color: color || "black",
        textAlign: align || "left",
        fontSize: 18,
        textDecorationLine: textDecoration,
      }}
    >
      {children}
    </h4>
  );
};

const sm = ({ children, color, align, textDecoration }) => {
  return (
    <p
      style={{
        color: color || "black",
        textAlign: align || "left",
        fontSize: 12,
        textDecorationLine: textDecoration,
      }}
    >
      {children}
    </p>
  );
};

const label = ({ children, color, align, textDecoration }) => {
  return (
    <p
      style={{
        color: color || "black",
        textAlign: align || "left",
        fontSize: 18,
        fontWeight: 300,
        textDecorationLine: textDecoration,
      }}
    >
      {children}
    </p>
  );
};

const hint = ({ children, color, align, textDecoration }) => {
  return (
    <p
      style={{
        color: color || "#38C0F1",
        textAlign: align || "left",
        fontSize: 12,
        fontWeight: 300,
        textDecorationLine: textDecoration,
      }}
    >
      {children}
    </p>
  );
};

const error = ({ children, color, textDecoration, align }) => {
  return (
    <p
      style={{
        color: color || "red",
        textAlign: align || "left",
        fontSize: 14,
        fontWeight: 600,
        textDecorationLine: textDecoration,
      }}
    >
      {children}
    </p>
  );
};

const placeholder = ({ children, color, textDecoration }) => {
  return (
    <p
      style={{
        color: color || "#ABABAB",
        fontSize: 14,
        textDecorationLine: textDecoration,
      }}
    >
      {children}
    </p>
  );
};

export { h1, h2, h3, h4, sm, label, hint, error, placeholder };
