import React from "react";
import { useParams } from "react-router-dom";
import products from "../data/products";
import { Header, Footer } from "../components/mollecules";
import { Cards, Text } from "../components/atomes";

function Product() {
  const { id } = useParams();

  // Recherche du produit par ID
  const product = products.find((item) => item.id === id);

  if (!product) {
    return (
      <div className="min-h-screen flex flex-col justify-between">
        <div>
          <Header.LaptopHeader />
          <div className="m-20">
            <Text.error align={"center"}>Produit non trouv√©...</Text.error>
          </div>
        </div>
        <div>
          <Footer.laptopFooter />
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="min-h-screen flex flex-col justify-between">
        <div>
          <Header.LaptopHeader />
          <Cards.Produit />
        </div>
        <div>
          <Footer.laptopFooter />
        </div>
      </div>
    </div>
  );
}

export default Product;
