import React from "react";
import { useParams } from "react-router-dom";
import products from "../data/products";
import { Header, Footer } from "../components/mollecules";
import { Button, Cards, Text } from "../components/atomes";

const produitVinRouge = require("../assets/produitVinRouge.png");
const produitVinBlanc = require("../assets/produitVinBlanc.png");
const produitChampagne = require("../assets/produitChampagne.png");

function Product() {
  const { id } = useParams();

  const product = products.find((item) => item.id === id);

  if (!product) {
    return (
      <div className="min-h-screen flex flex-col justify-between">
        <div>
          <Header.LaptopHeader />
          <div className="m-20">
            <Text.error align={"center"}>Produit non trouv√©...</Text.error>
          </div>
        </div>
        <div>
          <Footer.laptopFooter />
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="min-h-screen flex flex-col justify-between">
        <Header.LaptopHeader />
        <div className="mx-20 flex flex-row justify-between items-center">
          <Cards.Produit
            id={product.id}
            bg={
              product.type === "Rouge"
                ? produitVinRouge
                : product.type === "Blanc"
                ? produitVinBlanc
                : product.type === "Champagne"
                ? produitChampagne
                : null
            }
            color={product.color}
            typeColor={product.typeColor}
            type={product.type}
            name={product.name}
            pays={product.pays}
            oldPrice={product.oldPrice}
            price={product.price}
          />
          <div className="w-1/2">
            <p>
              Profiter d'une bouteille de {product.name} au prix de
              {product.price} au lieu de {product.oldPrice}
            </p>
            <div className="mt-6">
              <Button.Primary value={"Ajouter au panier"} />
            </div>
          </div>
        </div>
        <Footer.laptopFooter />
      </div>
    </div>
  );
}

export default Product;
