import { Button, Text } from ".";

const Search = ({
  label,
  placeholder,
  onChange,
  type,
  value,
  tags,
  setTags,
}) => {
  const addTags = () => {
    if (value !== "") {
      setTags([...tags, value]);
      onChange("");
    }
  };

  return (
    <div className="w-1/2">
      <Text.label className="text-black bg-transparent">{label}</Text.label>
      <div className="flex w-full items-center border-2 px-2">
        <input
          value={value}
          type={type || "text"}
          className="w-11/12 p-1 bg-white"
          placeholder={placeholder}
          onChange={(e) => onChange(e.target.value)}
          onKeyDown={(key) => {
            if (key.code === "Enter") {
              addTags();
            }
          }}
          onBlur={() => addTags()}
        />
        <div className="material-icons" onClick={() => addTags()}>
          search
        </div>
      </div>
      <div
        className={`flex-row w-full p-1 mt-1 ${
          tags.length !== 0 ? "flex" : "hidden"
        }`}
      >
        {tags.map((i) => {
          return (
            <div
              key={i}
              className="flex flex-row items-center justify-between p-1 mr-2 text-white bg-black border-solid rounded-md shadow-md border-1 border-zinc shadow-zinc-400"
            >
              <Text.label className="text-white">{i}</Text.label>
              <div
                className="text-sm cursor-pointer material-icons"
                onClick={() => {
                  setTags((current) => current.filter((items) => items !== i));
                }}
              >
                close
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export { Search };
