import React from "react";
import { useParams } from "react-router-dom";
import products from "../data/products";
import { Header, Footer } from "../components/mollecules";
import { Cards, Text } from "../components/atomes";

const produitVinRouge = require("../assets/produitVinRouge.png");
const produitVinBlanc = require("../assets/produitVinBlanc.png");
const produitChampagne = require("../assets/produitChampagne.png");

function Product() {
  const { id } = useParams();

  const product = products.find((item) => item.id === id);

  if (!product) {
    return (
      <div className="min-h-screen flex flex-col justify-between">
        <div>
          <Header.LaptopHeader />
          <div className="m-20">
            <Text.error align={"center"}>Produit non trouv√©...</Text.error>
          </div>
        </div>
        <div>
          <Footer.laptopFooter />
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="min-h-screen flex flex-col justify-between">
        <Header.LaptopHeader />
        <div className="mx-20 flex flex-row justify-between items-center">
          <Cards.Produit
            id={product.id}
            bg={
              product.type === "Rouge"
                ? produitVinRouge
                : product.type === "Blanc"
                ? produitVinBlanc
                : product.type === "Champagne"
                ? produitChampagne
                : null
            }
            color={product.color}
            typeColor={product.typeColor}
            type={product.type}
            name={product.name}
            pays={product.pays}
            oldPrice={product.oldPrice}
            price={product.price}
          />
          <div className="">
            <p>
              Lorem, ipsum dolor sit amet consectetur adipisicing elit. Nobis et
              obcaecati ullam in eaque cupiditate culpa accusantium eos. Ea
              asperiores nihil sequi numquam voluptates repudiandae quam minus?
              Ratione praesentium quos illo sed incidunt doloribus laborum
              voluptatibus necessitatibus a enim repellat harum sunt optio
              eveniet, tenetur nostrum sapiente inventore beatae eaque,
              dignissimos et aliquam doloremque quasi. Delectus ab numquam omnis
              quisquam molestias, deserunt, recusandae accusamus fuga voluptates
              mollitia veritatis, modi assumenda. Cupiditate id praesentium ab
              blanditiis ratione sint ipsa qui itaque inventore pariatur quae
              facilis deserunt soluta maxime nisi velit esse laboriosam
              voluptatibus earum, eius at asperiores aperiam consequatur! Neque,
              velit!
            </p>
          </div>
        </div>
        <Footer.laptopFooter />
      </div>
    </div>
  );
}

export default Product;
