import React from "react";
import { useParams } from "react-router-dom";
import products from "../data/products";
import { Header, Footer } from "../components/mollecules";
import { Cards, Text } from "../components/atomes";

function Product() {
  const { id } = useParams();

  const product = products.find((item) => item.id === id);

  if (!product) {
    return (
      <div className="min-h-screen flex flex-col justify-between">
        <div>
          <Header.LaptopHeader />
          <div className="m-20">
            <Text.error align={"center"}>Produit non trouv√©...</Text.error>
          </div>
        </div>
        <div>
          <Footer.laptopFooter />
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="min-h-screen flex flex-col justify-between">
        <div>
          <Header.LaptopHeader />
          <Cards.Produit
            id={product.id}
            bg={
              product.type === "Rouge"
                ? produitVinRouge
                : product.type === "Blanc"
                ? produitVinBlanc
                : product.type === "Champagne"
                ? produitChampagne
                : null
            }
            color={product.color}
            typeColor={product.typeColor}
            type={product.type}
            name={i.name}
            pays={i.pays}
            oldPrice={i.oldPrice}
            price={i.price}
          />
        </div>
        <div>
          <Footer.laptopFooter />
        </div>
      </div>
    </div>
  );
}

export default Product;
