import React, { useState } from "react";
import { Text, Button } from "../atomes";

const Carousel = () => {
  const [display, setDisplay] = useState(1);

  const changeDisplay = () => {
    if (display === 1) {
      setDisplay(2);
    } else if (display === 2) {
      setDisplay(1);
    }
  };

  return (
    <div className="w-full h-[80vh] bg-red-300">
      <div className="flex flex-row justify-between relative top-1/2 px-10">
        <Button.Left onClick={() => changeDisplay()} />
        <Button.Right onClick={() => changeDisplay()} />
      </div>
      <div className="select-none bg-red-700 inline absolute top-1/3 left-8">
        <div>
          {display === 1 ? (
            <Text.h3 color={"white"} fontSize={50}>
              Nouveautés 2023
            </Text.h3>
          ) : (
            <Text.h3 color={"white"} fontSize={50}>
              En Promotions
            </Text.h3>
          )}
        </div>
        <div>
          {display === 1 ? (
            <Text.label color={"white"} fontSize={32}>
              Dom Pérignon
            </Text.label>
          ) : (
            <Text.label color={"white"} fontSize={32}>
              Gaillac 2020
            </Text.label>
          )}
        </div>
        <div className="mt-4">
          <Button.Primary value={"Voir Plus"} />
        </div>
      </div>
    </div>
  );
};

export { Carousel };
