import React, { useContext, useState, useEffect } from "react";
import client from "../api/client";
import { v4 as uuidv4 } from "uuid";

const PanierContext = React.createContext();

export function PanierContextProvider({ children }) {
  const [panier, setPanier] = useState([
    {
      id: uuidv4(),
      color: "#5C221E",
      typeColor: "#5C221E",
      type: "Rouge",
      name: "Vin Rouge Corvo",
      pays: "Italie",
      oldPrice: "18.80€",
      price: "16.80€",
    },
    {
      id: uuidv4(),
      color: "#FFECB3",
      typeColor: "#FFECB3",
      type: "Blanc",
      name: "Vin Blanc Grigio",
      pays: "Italie",
      oldPrice: "29.99€",
      price: "24.99€",
    },
    {
      id: uuidv4(),
      color: "#D0943A",
      typeColor: "#D0943A",
      type: "Champagne",
      name: "Champagne Pinot",
      pays: "France",
      oldPrice: "29.90€",
      price: "27.90€",
    },
  ]);

  return (
    <PanierContext.Provider
      value={{
        panier,
        setPanier,
      }}
    >
      {children}
    </PanierContext.Provider>
  );
}

export const usePanier = () => useContext(PanierContext);
